// @flow

import type {
  IRawOptions, IRequest, IResponse
} from './interface'

import { isUndefined } from 'lodash'
import Response from './response'
import Request from './request'

export default function gen (reqOpts: ?IRawOptions, resOpts: ?IRawOptions, next: ?Function): {req: IRequest, res: IResponse, next: Function} {
  const req = new Request(reqOpts)
  const res = new Response(resOpts)

  if (reqOpts && resOpts && isUndefined(reqOpts.res) && isUndefined(resOpts.req)) {
    req.res = res
    res.req = req
  }

  return {
    req,
    res,
    next: next || (() => {})
  }
}

// @flow

import type {
  IAny,
  IResponse,
  IRequest,
  IRawOptions,
  IApp,
  IConnection,
  IData,
  IQuery,
  IParamsMap,
  IHeadersMap,
  IHeaderSetter,
  IUrl,
  ICookieSetter,
  ISocket
} from './interface'

import express from 'express'
import url from 'url'
import {
  assign,
  setprototypeof,
  appendAdditionalProps
} from './util'
import DEFAULT_APP from './app'

// $FlowFixMe
const { request, response } = express

export default class Request implements IRequest {
  $key: string

  $value: IAny

  cookie: ICookieSetter

  header: IHeaderSetter

  app: IApp | Object

  res: IResponse | Object

  connection: IConnection

  body: ?IData

  query: ?IQuery

  params: ?IParamsMap

  headers: IHeadersMap

  _flush: Function

  _readableState: IAny

  socket: ISocket

  constructor (input: ?IRawOptions) {
    setprototypeof(this, request)
    const opts = new ReqOptions(input || {})

    this._flush = () => {}
    this._readableState = {}
    this.socket = opts.socket
    this.app = opts.app
    this.res = opts.res
    this.headers = opts.headers
    this.body = opts.body
    this.params = opts.params
    this.connection = opts.connection

    // Passes additional props
    appendAdditionalProps(this, opts.raw)
  }
}

export class ReqOptions {
  raw: IRawOptions

  headers: IHeadersMap

  app: IApp | Object

  res: IResponse | Object

  connection: IConnection

  body: ?IData

  query: ?IQuery

  params: ?IParamsMap

  socket: ISocket

  constructor (input: IRawOptions) {
    this.res = input.res || {}
    this.app = input.app || DEFAULT_APP

    const headers = {}
    const urlStr: string = input.url || '/'
    const urlOpts = input.host
      ? input
      : url.parse(urlStr) // eslint-disable-line

    // NOTE host & protocol are required by URL constructor
    urlOpts.host = urlOpts.host || 'localhost'
    urlOpts.protocol = urlOpts.protocol || 'http'

    const urlData: IUrl = new url.URL(url.format(urlOpts))
    const connection = assign(
      { encrypted: urlData.protocol === 'https:' },
      input.connection || {}
    )
    const context = {
      set (k: string, v: string): void { headers[k] = v }
    }
    response.header.call(context, input.headers)
    headers.host = headers.host || urlData.host

    this.headers = headers
    // $FlowFixMe
    this.query = urlData.query
    this.body = input.body
    this.params = input.params || {}
    this.connection = connection
    this.raw = input
    this.socket = input.socket || {}
  }
}

// @flow

import type {
  IAny,
  IApp,
  IData,
  IStatusCode,
  IHeadersMap,
  IRequest,
  IResponse,
  IRawOptions,
  IResponseOpts,
  ICookie,
  ICookiesMap,
  ICookieSetter,
  IHeaderSetter,
  IStatusSetter,
  IDescriptor
} from './interface'

import express from 'express'
import {
  assign,
  each,
  isString,
  isBuffer,
  concat,
  setprototypeof,
  appendAdditionalProps
} from './util'
import DEFAULT_APP from './app'

// $FlowFixMe
const { response } = express

type Record<T, V> = {
  [T]: V
}

export const DEFAULT_STATUS_CODE = 200
export const DEFAULT_HEADERS: Record<any, any> = {}
export const DEFAULT_COOKIES: Record<any, any> = {}
export const DEFAULT_REQ: Record<any, any> = {}

export default class Response implements IResponse {
  $key: string

  $value: IAny

  cookie: ICookieSetter

  header: IHeaderSetter

  headers: IHeadersMap

  _headers: IAny

  status: IStatusSetter

  app: IApp

  req: IRequest | Object

  body: IDescriptor

  emit: Function

  write: Function

  end: Function

  constructor (input: ?IRawOptions) {
    setprototypeof(this, response)
    const opts = new ResOptions(input || {})

    let body: IData
    Object.defineProperty(this, 'body', ({
      get () { return body },
      set (value: IData) { throw new Error('Use .send(), .write() or .json()') }
    }: Object))

    const write = this.write.bind(this)
    this.write = (chunk: IData, encoding?: ?string, callback?: ?Function): IResponse => {
      if (isBuffer(chunk)) {
        body = concat(body, chunk.toString(encoding))
      } else if (isString(chunk)) {
        body = concat(body, chunk)
      }
      if (chunk !== undefined) {
        write(chunk, encoding, callback)
      }
      return this
    }
    this.end = (chunk: IData, encoding?: ?string): IResponse => {
      if (chunk) {
        body = chunk
      }
      this.emit('finish')
      return this
    }
    this._headers = null
    this.req = opts.req
    this.app = opts.app
    this.status(opts.statusCode)
    this.header(opts.headers)

    each(opts.cookies, ({ name, value, options }: ICookie): void => this.cookie(name, value, options))

    // Passes additional props
    appendAdditionalProps(this, opts.raw)
  }
}

export class ResOptions implements IResponseOpts {
  raw: IRawOptions

  statusCode: IStatusCode

  headers: IHeadersMap

  cookies: ICookiesMap

  app: IApp | Object

  req: IRequest | Object

  constructor (input: IRawOptions) {
    this.raw = input
    this.statusCode = ((input.statusCode || input.status || DEFAULT_STATUS_CODE) | 0)
    this.headers = assign({}, DEFAULT_HEADERS, input.headers || {})
    this.cookies = assign({}, DEFAULT_COOKIES, input.cookies || {})
    this.app = input.app || DEFAULT_APP
    this.req = input.req || DEFAULT_REQ
  }
}

