import reqresnext from '../../main/js/reqresnext'

describe('reqresnext', () => {
  it('generates proper result map', () => {
    const { req, res, next } = reqresnext({
      method: 'GET',
      url: '/example.com'
    })

    expect(req).toEqual(expect.any(Object))
    expect(res).toEqual(expect.any(Object))
    expect(next).toEqual(expect.any(Function))
  })

  describe('req-res cross links:', () => {
    it('are added if not specified in the opts', () => {
      const { req, res } = reqresnext({}, {})

      expect(req.res).toEqual(res)
      expect(res.req).toEqual(req)
    })

    it('are skipped otherwise', () => {
      const foo = {}
      const bar = {}
      const { req, res } = reqresnext({ res: foo }, { req: bar })

      expect(req.res).toEqual(foo)
      expect(res.req).toEqual(bar)
    })
  })

  describe('next', () => {
    it('creates empty fn if param is undefined', () => {
      const { next } = reqresnext()

      expect(next).toEqual(expect.any(Function))
      expect(next()).toBeUndefined()
    })

    it('passes back optional handler', () => {
      const handler = () => {}
      const { next } = reqresnext({}, {}, handler)

      expect(next).toBe(handler)
    })
  })
})

import Request from '../../main/js/request'
import { IncomingMessage } from 'http'

describe('request', () => {
  describe('constructor', () => {
    it('returns proper instance', () => {
      const req = new Request()

      // NOTE Yes, it's correct. You can rely on the fact that req.constructor refers to IncomingMessage.
      expect(req).not.toBeInstanceOf(Request)
      expect(req).toBeInstanceOf(IncomingMessage)
    })
  })

  describe('proto', () => {
    describe('url', () => {
      it('exposes path parts as getters', () => {
        const req = new Request({ url: 'https://example.com' })

        expect(req.hostname).toBe('example.com')
        expect(req.protocol).toBe('https')
      })

      it('opts form has priority', () => {
        const req = new Request({ host: 'foobar.com', protocol: 'http', url: 'https://example.com' })

        expect(req.path).toBe('/')
        expect(req.hostname).toBe('foobar.com')
        expect(req.host).toBe('foobar.com')
        expect(req.protocol).toBe('http')
      })

      it('attaches default host and protocol values', () => {
        const req = new Request({ url: '/example.com' })

        expect(req.url).toBe('/example.com')
        expect(req.host).toBe('localhost')
        expect(req.protocol).toBe('http')
      })
    })

    describe('headers', () => {
      it('exposes proper getters', () => {
        const req = new Request({ headers: { foo: 'bar', baz: 'qux' } })

        expect(req.headers.foo).toBe('bar')
        expect(req.header('foo')).toBe('bar')
        expect(req.get('BAZ')).toBe('qux')
      })
    })

    describe('ip', () => {
      it('resolves value from headers', () => {
        const req = new Request({
          headers: {
            'x-forwarded-for': '192.168.1.10'
          },
          method: 'GET'
        })

        expect(req.ip).toBe('192.168.1.10')
      })
    })

    describe('method', () => {
      it('is passed down', () => {
        const req = new Request({
          method: 'GET'
        })
        expect(req.method).toBe('GET')
      })
    })

    describe('events', () => {
      it('on', () => {
        let foo
        const req = new Request()
        req.on('data', () => {
          foo = 'bar'
        })
        req.emit('data')

        expect(foo).toBe('bar')
      })
    })

    describe('_flush', () => {
      it('does nothing', () => {
        const req = new Request()

        expect(req._flush()).toBeUndefined()
      })
    })
  })
})

import Response from '../../main/js/response'
import { ServerResponse } from 'http'

describe('response', () => {
  describe('construtor', () => {
    it('returns proper instance', () => {
      const res = new Response()

      expect(res).not.toBeInstanceOf(Response)
      expect(res).toBeInstanceOf(ServerResponse)
    })

    it('inherits http.ServerResponse', () => {
      const res = new Response()

      expect(res.pipe).toEqual(expect.any(Function))
      expect(res.setHeader).toEqual(expect.any(Function))
      expect(res.getHeader).toEqual(expect.any(Function))
    })

    it('passes optional props', () => {
      const end = () => {}
      const res = new Response({ foo: 'bar', end })

      expect(res.foo).toBe('bar')
      expect(res.end).not.toEqual(end)
    })
  })

  describe('proto', () => {
    it('send', () => {
      const res = new Response()
      res.send('foo')
      expect(res.body).toBe('foo')
    })

    it('json', () => {
      const res = new Response()
      const data = { foo: 'bar' }
      res.json(data)

      expect(res.body).toEqual(JSON.stringify(data))
    })

    it('body', () => {
      const res = new Response()
      res.send('foo')
      expect(res.body).toBe('foo')

      expect(() => {
        res.body = 'bar'
      }).toThrow('Use .send(), .write() or .json()')
    })

    it('cookies', () => {
      const foo = { name: 'foo', value: 'bar', options: {} }
      const res = new Response({ cookies: [foo] })

      expect(res.get('Set-Cookie')).toBe('foo=bar; Path=/')
    })

    it('on', () => {
      let foo
      const res = new Response()
      res.on('finish', () => {
        foo = 'bar'
      })

      res.send('')

      expect(foo).toBe('bar')
    })

    it('write', () => {
      const res = new Response()
      res.write('foo')
      res.write(Buffer.from('bar'))
      res.write()
      expect(res.body).toBe('foobar')
    })
  })
})
